import React, { Component } from "react";
import axios from "axios";

class PostForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      espacioID: "",
      espaciosDisponibles: [],
      espaciosOcupados: [],
    };
  }
  clickMe() {
    alert("You clicked me!");
  }

  espaciosOcupados() {
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((response) => {
        console.log(response);
        this.setState({ espaciosDisponibles: response.data });

      })
      .catch((error) => console.log(error));
  }

  espaciosDisponibles() {
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((response) => {
        console.log(response);
        this.setState({ espaciosDisponibles: response.data });

      })
      .catch((error) => console.log(error));
  }

  reservarEspacio() {}

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = (e) => {
    e.preventDefault();
    console.log(this.state);
  };

  render() {
    const { espacioID, espaciosDisponibles, espaciosOcupados } = this.state;
    return (
      <>
        <div>
          <h1>Consultar Espacios Disponibles</h1>
          <button onClick={this.espaciosDisponibles}>Consultar</button>
          <div>
            {espaciosDisponibles.length
              ? espaciosDisponibles.map((espacio) => (
                  <div key={espacio.id}> {espacio.title}</div>
                ))
              : null}
          </div>
        </div>
        <div>
          <h1>Consultar Espacios Ocupados</h1>
          <button onClick={this.clickMe}>Consultar</button>
        </div>
        <div>
          <h1>Reservar Espacio</h1>
          <button onClick={this.clickMe}>Reservar</button>
        </div>
        <div>
          <h1>Liberar Espacios</h1>
          <div>
            <div>
              <form onSubmit={this.submitHandler}>
                <h3>Ingrese el ID del espacio</h3>

                <input
                  type="number"
                  name="espacioID"
                  value={espacioID}
                  onChange={this.changeHandler}
                />
                <button type="submit">Liberar</button>
              </form>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default PostForm;
